import React, { Component } from "react";



class Clock extends Component {
  constructor(props){
    super(props);
    this.state = {
      date: new Date()
    };
  }
  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
  }
  componentWillMount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }

  render() {
    return (
      <div>
        <h2>The local time is {this.state.date.toLocaleTimeString()}</h2>
      </div>
    );
  }
}

export default Clock;
